    //Sql tables

        CREATE TABLE  estado (
          id int(11) NOT NULL,
          nome varchar(75) DEFAULT NULL,
          uf varchar(5) DEFAULT NULL,
          PRIMARY KEY (id)
        );



        CREATE TABLE cidade (
        id int(11) NOT NULL,
        nome varchar(120) DEFAULT NULL,
        estado int(5) DEFAULT NULL,
        PRIMARY KEY (id)
        );



        DROP TABLE IF EXISTS estado;



        DROP TABLE IF EXISTS cidade;



